'use strict';

var core = require('@tauri-apps/api/core');

async function getItem(key) {
    return await core.invoke('plugin:keychain|get_item', {
        key,
    }).then((r) => (r.password ? r.password : null));
}
async function saveItem(key, password) {
    return await core.invoke('plugin:keychain|save_item', {
        key,
        password
    });
}
async function removeItem(key) {
    return await core.invoke('plugin:keychain|remove_item', {
        key,
    });
}

exports.getItem = getItem;
exports.removeItem = removeItem;
exports.saveItem = saveItem;
