import { invoke } from '@tauri-apps/api/core';

async function getItem(key) {
    return await invoke('plugin:keychain|get_item', {
        key,
    }).then((r) => (r.password ? r.password : null));
}
async function saveItem(key, password) {
    return await invoke('plugin:keychain|save_item', {
        key,
        password
    });
}
async function removeItem(key) {
    return await invoke('plugin:keychain|remove_item', {
        key,
    });
}

export { getItem, removeItem, saveItem };
